class Admin_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get admin by id_admin
     */
    function get_admin($id_admin)
    {
        return $this->db->get_where('admins',array('id_admin'=>$id_admin))->row_array();
    }
        
    /*
     * Get all admins
     */
    function get_all_admins()
    {
        $this->db->order_by('id_admin', 'desc');
        return $this->db->get('admins')->result_array();
    }
        
    /*
     * function to add new admin
     */
    function add_admin($params)
    {
        $this->db->insert('admins',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update admin
     */
    function update_admin($id_admin,$params)
    {
        $this->db->where('id_admin',$id_admin);
        return $this->db->update('admins',$params);
    }
    
    /*
     * function to delete admin
     */
    function delete_admin($id_admin)
    {
        return $this->db->delete('admins',array('id_admin'=>$id_admin));
    }
}
